// src/components/Dashboard.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaTools, FaSearch, FaCalendarAlt, FaCog, FaExclamationTriangle } from 'react-icons/fa';
import { Menu } from '@headlessui/react';
import './Dashboard.css';

const Dashboard = () => {
  const [maintenanceData, setMaintenanceData] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [filteredData, setFilteredData] = useState([]);
  
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMaintenanceRecords = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/maintenance');
        setMaintenanceRecords(response.data);
      } catch (error) {
        console.error('Error fetching maintenance records:', error);
        setError('Error fetching maintenance records');
      }
    };

    fetchMaintenanceRecords();
  }, []);

  useEffect(() => {
    setFilteredData(
      maintenanceData.filter(data =>
        data.model.toLowerCase().includes(searchQuery.toLowerCase()) ||
        data.part_name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        data.technician_name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        data.critical_spare_parts.toLowerCase().includes(searchQuery.toLowerCase()) ||
        data.maintenance_date.toLowerCase().includes(searchQuery.toLowerCase())
      )
    );
  }, [searchQuery, maintenanceData]);

  return (
    <div className="dashboard-container">
      <div className="dashboard-content">
        <header className="dashboard-header">
          <h1 className="dashboard-title">Maintenance Dashboard</h1>
          <Menu as="div" className="options-menu">
            <Menu.Button className="options-button">
              Options <FaTools className="menu-icon" />
            </Menu.Button>
            <Menu.Items className="menu-items">
              <Menu.Item>
                {({ active }) => (
                  <a href="#" className={`menu-item ${active ? 'active' : ''}`}>
                    Export Data
                  </a>
                )}
              </Menu.Item>
              <Menu.Item>
                {({ active }) => (
                  <a href="#" className={`menu-item ${active ? 'active' : ''}`}>
                    Settings
                  </a>
                )}
              </Menu.Item>
            </Menu.Items>
          </Menu>
        </header>

        <div className="search-container">
          <div className="search-wrapper">
            <input
              type="text"
              className="search-input"
              placeholder="Search by Machine ID, Model, Technician..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <FaSearch className="search-icon" />
          </div>
        </div>

        <div className="status-section">
          <div className="status-card total-machines">
            <FaCog className="status-icon" />
            <h3>Total Machines</h3>
            <p>{maintenanceData.length}</p>
          </div>
          <div className="status-card upcoming-maintenance">
            <FaCalendarAlt className="status-icon" />
            <h3>Upcoming Maintenance</h3>
            <p>{filteredData.filter(data => new Date(data.next_maintenance_date) > new Date()).length}</p>
          </div>
          <div className="status-card overdue-maintenance">
            <FaExclamationTriangle className="status-icon" />
            <h3>Overdue Maintenance</h3>
            <p>{filteredData.filter(data => new Date(data.next_maintenance_date) < new Date()).length}</p>
          </div>
        </div>

        {loading ? (
          <div className="loading">Loading data...</div>
        ) : (
          <div className="data-table-wrapper">
            <table className="data-table">
              <thead>
                <tr>
                  <th>Machine ID</th>
                  <th>Model</th>
                  <th>Next Maintenance Date</th>
                  <th>Technician Name</th>
                </tr>
              </thead>
              <tbody>
                {filteredData.map((data) => (
                  <tr key={data._id} className="data-row">
                    <td>{data.machine_id}</td>
                    <td>{data.model}</td>
                    <td>{data.next_maintenance_date}</td>
                    <td>{data.technician_name}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
