// src/components/Dashboard.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaTools, FaSearch, FaCalendarAlt, FaCog, FaExclamationTriangle } from 'react-icons/fa';
import { Menu } from '@headlessui/react';
import './Dashboard.css';


const Dashboard = () => {
  const [maintenanceData, setMaintenanceData] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [filteredData, setFilteredData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMaintenanceData = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/maintenance');
        setMaintenanceData(response.data);
        setFilteredData(response.data);
      } catch (error) {
        console.error("Error fetching maintenance data:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchMaintenanceData();
  }, []);

  useEffect(() => {
    setFilteredData(
      maintenanceData.filter(data =>
        data.model.toLowerCase().includes(searchQuery.toLowerCase()) ||
        data.part_name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        data.technician_name.toLowerCase().includes(searchQuery.toLowerCase()) ||
        data.critical_spare_parts.toLowerCase().includes(searchQuery.toLowerCase()) ||
        data.maintenance_date.toLowerCase().includes(searchQuery.toLowerCase())
      )
    );
  }, [searchQuery, maintenanceData]);

  return (
    <div className="bg-gradient-to-br from-black to-black min-h-screen p-6">
      <div className="container mx-auto">
        <header className="flex justify-between items-center py-6 mb-4 bg-blue-600 text-white rounded-lg shadow-md px-8">
          <h1 className="text-4xl font-extrabold">Maintenance Dashboard</h1>
          <Menu as="div" className="relative">
            <Menu.Button className="flex items-center px-4 py-2 bg-blue-700 rounded-lg hover:bg-blue-800">
              Options <FaTools className="ml-2" />
            </Menu.Button>
            <Menu.Items className="absolute right-0 mt-2 w-48 bg-white rounded-md shadow-lg ring-1 ring-black ring-opacity-5 focus:outline-none">
              <Menu.Item>
                {({ active }) => (
                  <a href="#" className={`block px-4 py-2 text-sm ${active ? 'bg-gray-100' : ''}`}>
                    Export Data
                  </a>
                )}
              </Menu.Item>
              <Menu.Item>
                {({ active }) => (
                  <a href="#" className={`block px-4 py-2 text-sm ${active ? 'bg-gray-100' : ''}`}>
                    Settings
                  </a>
                )}
              </Menu.Item>
            </Menu.Items>
          </Menu>
        </header>

        {/* Search bar */}
        <div className="flex justify-end mb-6">
          <div className="relative w-1/2">
            <input
              type="text"
              className="w-full p-3 pl-10 border border-blue-300 rounded-lg shadow focus:outline-none focus:ring-2 focus:ring-blue-500"
              placeholder="Search by Machine ID, Model, Technician..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
            />
            <FaSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-blue-500" />
          </div>
        </div>

        {/* Summary and Status Section */}
        <div className="grid grid-cols-3 gap-4 mb-6">
          <div className="bg-blue-200 p-6 rounded-lg shadow-lg flex flex-col items-center">
            <FaCog className="text-3xl text-blue-700 mb-2" />
            <h3 className="text-lg font-semibold text-blue-800">Total Machines</h3>
            <p className="text-2xl font-bold">{maintenanceData.length}</p>
          </div>
          <div className="bg-green-200 p-6 rounded-lg shadow-lg flex flex-col items-center">
            <FaCalendarAlt className="text-3xl text-green-700 mb-2" />
            <h3 className="text-lg font-semibold text-green-800">Upcoming Maintenance</h3>
            <p className="text-2xl font-bold">{filteredData.filter(data => new Date(data.next_maintenance_date) > new Date()).length}</p>
          </div>
          <div className="bg-red-200 p-6 rounded-lg shadow-lg flex flex-col items-center">
            <FaExclamationTriangle className="text-3xl text-red-700 mb-2" />
            <h3 className="text-lg font-semibold text-red-800">Overdue Maintenance</h3>
            <p className="text-2xl font-bold">{filteredData.filter(data => new Date(data.next_maintenance_date) < new Date()).length}</p>
          </div>
        </div>

        {/* Data Table */}
        {loading ? (
          <div className="flex justify-center mt-8">
            <p>Loading data...</p>
          </div>
        ) : (
          <div className="overflow-x-auto bg-white shadow-md rounded-lg">
            <table className="min-w-full border border-gray-200">
              <thead className="bg-blue-600 text-white">
                <tr>
                  <th className="px-6 py-3 text-left text-sm font-medium uppercase tracking-wider">Machine ID</th>
                  <th className="px-6 py-3 text-left text-sm font-medium uppercase tracking-wider">Model</th>
                  <th className="px-6 py-3 text-left text-sm font-medium uppercase tracking-wider">Next Maintenance Date</th>
                  <th className="px-6 py-3 text-left text-sm font-medium uppercase tracking-wider">Technician Name</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                {filteredData.map((data) => (
                  <tr key={data._id} className="hover:bg-gray-50 cursor-pointer">
                    <td className="px-6 py-4 text-sm font-medium text-gray-900">{data.machine_id}</td>
                    <td className="px-6 py-4 text-sm text-gray-700">{data.model}</td>
                    <td className="px-6 py-4 text-sm text-gray-700">{data.next_maintenance_date}</td>
                    <td className="px-6 py-4 text-sm text-gray-700">{data.technician_name}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
