import React, { useState } from 'react';
import './AddOrUpdateMaintenance.css';

const AddOrUpdateMaintenance = () => {
  const [form, setForm] = useState({
    machineName: '',
    model: '',
    nextMaintenanceDate: '',
  });

  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      const response = await fetch('http://localhost:5000/api/maintenance', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(form),
      });

      if (response.ok) {
        console.log("Maintenance record saved:", form);
        alert('Maintenance record saved successfully');
        setForm({ machineName: '', model: '', nextMaintenanceDate: '' });
      } else {
        alert('Error saving maintenance record');
      }
    } catch (error) {
      console.error("Error:", error);
      alert('Failed to save maintenance record');
    }
  };

  return (
    <div className="maintenance-form-container">
      <h1 className="title">Add/Update Maintenance</h1>
      <form onSubmit={handleSubmit} className="maintenance-form">
        <div className="form-group">
          <label className="label">Machine Name</label>
          <input
            type="text"
            name="machineName"
            value={form.machineName}
            onChange={handleChange}
            className="input"
            required
          />
        </div>
        <div className="form-group">
          <label className="label">Model</label>
          <input
            type="text"
            name="model"
            value={form.model}
            onChange={handleChange}
            className="input"
            required
          />
        </div>
        <div className="form-group">
          <label className="label">Next Maintenance Date</label>
          <input
            type="date"
            name="nextMaintenanceDate"
            value={form.nextMaintenanceDate}
            onChange={handleChange}
            className="input"
            required
          />
        </div>
        <button type="submit" className="submit-button">Save Maintenance</button>
      </form>
    </div>
  );
};

export default AddOrUpdateMaintenance;
