import React, { useState } from 'react';

const AddOrUpdateMaintenance = () => {
  const [form, setForm] = useState({
    machineName: '',
    model: '',
    nextMaintenanceDate: '',
  });

  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Perform POST or PUT request to save/update the data
    console.log("Maintenance record saved:", form);
    alert('Maintenance record saved successfully');
  };

  return (
    <div>
      <h1 className="text-2xl font-bold mb-4">Add/Update Maintenance</h1>
      <form onSubmit={handleSubmit} className="bg-white p-6 shadow-md rounded">
        <div className="mb-4">
          <label className="block text-sm font-semibold mb-2">Machine Name</label>
          <input
            type="text"
            name="machineName"
            value={form.machineName}
            onChange={handleChange}
            className="border p-2 w-full"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-sm font-semibold mb-2">Model</label>
          <input
            type="text"
            name="model"
            value={form.model}
            onChange={handleChange}
            className="border p-2 w-full"
            required
          />
        </div>
        <div className="mb-4">
          <label className="block text-sm font-semibold mb-2">Next Maintenance Date</label>
          <input
            type="date"
            name="nextMaintenanceDate"
            value={form.nextMaintenanceDate}
            onChange={handleChange}
            className="border p-2 w-full"
            required
          />
        </div>
        <button type="submit" className="bg-blue-600 text-white py-2 px-4 rounded">
          Save Maintenance
        </button>
      </form>
    </div>
  );
};

export default AddOrUpdateMaintenance;
